import React, { useState } from 'react';
import trash_can from '../assets/trash_can.png';

function BulletPoint(props) {

    const [isclicked, setIsClicked] = useState(false);
    const [mouseOver, setMouseOver] = useState(false);
    const [imgHover, setImgHover] = useState(false);

    function handleClick() {
        setIsClicked((prevValue) => {
            return (!prevValue)
        })
    }

    function hovering() {
        setMouseOver(true);
        console.log(mouseOver);
    }

    function notHovering() {
        setMouseOver(false);
    }

    function imgHov() {
        setImgHover(true);
    }

    function imgHold() {

        if (imgHover === true && mouseOver === true) {
            return (
                <img onMouseOver={imgHov} src={trash_can} alt="trash can icon" className='bulletItemImage' />
            )
        }
        // { imgHover && <img onMouseOver={imgHov} src={trash_can} alt="trash can icon" className='bulletItemImage' /> }
    }

    return (
        <div className='bulletItem' >
            <div onMouseOver={hovering} onMouseOut={notHovering} >
                <ul>
                    <li onClick={handleClick} style={{ textDecoration: isclicked && "line-through" }} >
                        {props.bullet}
                    </li>
                </ul>
                {mouseOver && imgHold}
            </div>

        </div>
    )
}

export default BulletPoint;


